import React, { useState, useEffect, useCallback } from "react";
import { useDispatch, useSelector } from "react-redux";
import { MIN_WIDTH, MIN_HEIGHT } from "../../constants";
import {
  hideSettings,
  setGame,
  restartGame,
} from "../../store/modules/control";
import { Settings } from "../../components";

const SettingsContainer = () => {
  const dispatch = useDispatch();
  const enableSettings = useSelector(
    (rootState) => rootState.control.enableSettings
  );

  const [width, setWidth] = useState(MIN_WIDTH);
  const [height, setHeight] = useState(MIN_HEIGHT);

  const onChangeWidth = useCallback((e) => {
    setWidth(parseInt(e.target.value));
  }, []);

  const onChangeHeight = useCallback((e) => {
    setHeight(parseInt(e.target.value));
  }, []);

  const onClickSet = useCallback(() => {
    dispatch(setGame(width, height));
    dispatch(restartGame());
    dispatch(hideSettings());
  }, [width, height]);

  return (
    <>
      {enableSettings && (
        <Settings
          width={width}
          height={height}
          onChangeWidth={onChangeWidth}
          onChangeHeight={onChangeHeight}
          onClickSet={onClickSet}
        />
      )}
    </>
  );
};

export default SettingsContainer;
