import React, { useState, useEffect } from "react";
import "./ScheduleCd.css";

// 행과 열의 갯수를 정의합니다.
const numRows = 7;
const numCols = 10;
const stringArray = [
  // 문자열 배열을 정의합니다.
  // ... (원하는 문자열을 넣어주세요)
];

// 빈 보드를 생성하는 함수입니다.
const generateEmptyBoard = () => {
  const board = [];
  for (let i = 0; i < numRows; i++) {
    // 각 행을 빈 배열로 초기화하여 2차원 배열을 만듭니다.
    board.push(Array(numCols).fill(undefined));
  }
  return board;
};

const ScheduleCd = () => {
  // 상태 변수를 사용하여 현재의 보드와 입력 값을 관리합니다.
  const [boardIndex, setBoardIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      // 1초마다 boardIndex를 업데이트합니다.
      setBoardIndex((prevIndex) => (prevIndex + 1) % stringArray.length);
    }, 1000);

    return () => {
      // 컴포넌트가 언마운트되면 interval을 클리어합니다.
      clearInterval(interval);
    };
  }, []); // useEffect는 컴포넌트 마운트 시에만 실행되도록 빈 배열을 전달합니다.

  const currentString = stringArray[boardIndex];

  const getImagePath = (value) => {
    switch (value) {
      case "H":
        return process.env.PUBLIC_URL + "/hazard.png";
      case "C":
        return process.env.PUBLIC_URL + "/colorblob.png";
      case "R":
        return process.env.PUBLIC_URL + "/robot.png";
      case "P":
        return process.env.PUBLIC_URL + "/point.png";
      case "c": // 추가된 부분
      case "h": // 추가된 부분
        return undefined; // 빈 문자열 반환
      default:
        return "0";
    }
  };

  useEffect(() => {
    // 컴포넌트가 처음 마운트될 때만 실행되는 부분입니다.
    // 초기 보드를 설정합니다.
    const newBoard = [...board];
    setBoard(newBoard);
  }, []);

  // 입력 값이 변경될 때 호출되는 함수입니다.
  const handleInputChange = (value) => {
    // 입력 값을 상태에 반영합니다.
    setInputSequence(value);
  };

  // 입력 값을 처리하고 보드를 업데이트하는 함수입니다.
  const handleInputSubmit = () => {
    let result = inputSequence.split(" ").join("");
    console.log(result.length);
    console.log(result);

    // 입력된 문자열이 유효한지 확인합니다.
    if (/^[HCcRp.]+$/i.test(result) && result.length === numRows * numCols) {
      // 유효한 경우, 새로운 보드를 생성하고 입력 값을 보드에 채웁니다.
      const newBoard = generateEmptyBoard();
      for (let i = 0; i < result.length; i++) {
        const inputValue = result[i];
        const [row, col] = getRowColFromIndex(i);
        newBoard[row][col] = inputValue;
      }
      // 업데이트된 보드를 상태에 반영합니다.
      setBoard(newBoard);
      // 에러 메시지를 초기화합니다.
      setErrorMessage("");
    } else {
      // 유효하지 않은 경우, 에러 메시지를 설정합니다.
      setErrorMessage(
        "Invalid input. Please enter valid characters (H, C, R, P, c, h) and make sure the input length is correct."
      );
    }
  };

  // 인덱스를 행과 열로 변환하는 함수입니다.
  const getRowColFromIndex = (index) => {
    const row = Math.floor(index / numCols);
    const col = index % numCols;
    return [row, col];
  };

  return (
    <div>
      {/* ... */}
      <table className="mapBoard">
        <tbody>
          {/* ... */}
          {board.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {row.map((cell, colIndex) => (
                <td key={colIndex}>
                  {/* ... */}
                  <td key={`c${colIndex}`} style={{ position: "relative" }}>
                    {currentString && (
                      <React.Fragment>
                        {getImagePath(currentString[colIndex]) && (
                          <img
                            src={getImagePath(currentString[colIndex])}
                            alt={`Cell at (${rowIndex}, ${colIndex})`}
                            className={`cell-image cell-image-${currentString[colIndex]}`}
                          />
                        )}
                      </React.Fragment>
                    )}
                  </td>
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      {/* ... */}
    </div>
  );
};

export default ScheduleCd;
