// App.js

import "./App.css";
import React, { useState } from "react";
import ScheduleCd from "./ScheduleCd";
import AudioRecord from "./audioRecode";

function App() {
  const [isPaused, setPaused] = useState(false);
  const [stoppedIndex, setStoppedIndex] = useState(null);

  const handleRecordingStop = (index) => {
    setPaused(true);
    setStoppedIndex(index);
  };

  return (
    <div>
      <ScheduleCd
        isPaused={isPaused}
        stoppedIndex={stoppedIndex}
        stringArray={[
          "HHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCC",
          "HHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHRRRRR",
          "HHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHPPPPP",
          "HHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHHHHHH",
          // ... (원하는 문자열을 추가해주세요)
        ]}
      />
      <AudioRecord setPause={setPaused} onRecordingStop={handleRecordingStop} />
    </div>
  );
}

export default App;
