import React, { useState, useCallback } from "react";
import ScheduleCd from "./ScheduleCd";

const App = () => {
  const [recordingIndex, setRecordingIndex] = useState(null);

  // recordingIndex를 설정하는 함수
  const handleRecordingStop = useCallback((index) => {
    setRecordingIndex(index);
  }, []);

  return (
    <div>
      <ScheduleCd recordingIndex={recordingIndex} />
      <AudioRecode onRecordingStop={handleRecordingStop} />
    </div>
  );
};

export default App;
