import React, { useState, useEffect } from "react";

const numRows = 5;
const numCols = 5;
// const numMines = 5; // 이 부분을 주석 처리하여 지뢰를 배치하지 않도록 합니다.

// 빈 보드를 생성하는 함수
const generateEmptyBoard = () => {
  const board = [];
  for (let i = 0; i < numRows; i++) {
    board.push(Array(numCols).fill(""));
  }
  return board;
};

// 보드에 지뢰를 무작위로 배치하는 함수
