import React, { useState, useEffect } from "react";
import "./ScheduleCd.css";

// 행과 열의 갯수를 정의합니다.
const numRows = 7;
const numCols = 10;

// 빈 보드를 생성하는 함수입니다.
const generateEmptyBoard = () => {
  const board = [];
  for (let i = 0; i < numRows; i++) {
    // 각 행을 빈 배열로 초기화하여 2차원 배열을 만듭니다.
    board.push(Array(numCols).fill(0));
  }
  return board;
};

const ScheduleCd = () => {
  // 상태 변수를 사용하여 현재의 보드와 입력 값을 관리합니다.
  const [board, setBoard] = useState(generateEmptyBoard());
  const [inputSequence, setInputSequence] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const getImagePath = (value) => {
    switch (value) {
      case "H":
        return "src/aImage/hazard.png";
      case "C":
        return "src/aImage/colorblob.png";
      case "R":
        return "src/aImage/robot.png";
      case "P":
        return "src/aImage/point.png";
      default:
        return "";
    }
  };

  useEffect(() => {
    // 컴포넌트가 처음 마운트될 때만 실행되는 부분입니다.
    // 초기 보드를 설정합니다.
    const newBoard = [...board];
    setBoard(newBoard);
  }, []);

  // 입력 값이 변경될 때 호출되는 함수입니다.
  const handleInputChange = (value) => {
    // 입력 값을 상태에 반영합니다.
    setInputSequence(value);
  };

  // 입력 값을 처리하고 보드를 업데이트하는 함수입니다.
  const handleInputSubmit = () => {
    let result = inputSequence.split(" ").join("");
    console.log(result.length);
    console.log(result);

    // 입력된 문자열이 유효한지 확인합니다.
    if (/^[HCcRp]+$/i.test(result) && result.length === numRows * numCols) {
      // 유효한 경우, 새로운 보드를 생성하고 입력 값을 보드에 채웁니다.
      const newBoard = generateEmptyBoard();
      for (let i = 0; i < result.length; i++) {
        const inputValue = result[i];
        const [row, col] = getRowColFromIndex(i);
        newBoard[row][col] = inputValue;
      }
      // 업데이트된 보드를 상태에 반영합니다.
      setBoard(newBoard);
      // 에러 메시지를 초기화합니다.
      setErrorMessage("");
    } else {
      // 유효하지 않은 경우, 에러 메시지를 설정합니다.
      setErrorMessage(
        "Invalid input. Please enter valid characters (H, C, R, P, c, h) and make sure the input length is correct."
      );
    }
  };

  // 인덱스를 행과 열로 변환하는 함수입니다.
  const getRowColFromIndex = (index) => {
    const row = Math.floor(index / numCols);
    const col = index % numCols;
    return [row, col];
  };

  return (
    <div>
      <h1>ScheduleCd</h1>
      {/* 보드를 테이블로 표시합니다. */}
      <table className="mapBoard">
        <tbody>
          {board.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {row.map((cell, colIndex) => (
                // 각 셀에는 이미지가 표시됩니다.
                <td key={colIndex}>
                  <img
                    src={getImagePath(cell)}
                    alt={`Cell at (${rowIndex}, ${colIndex})`}
                  />
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      {/* 에러 메시지를 표시합니다. */}
      {errorMessage && <div className="error">{errorMessage}</div>}

      {/* 입력을 받는 부분입니다. */}
      <div>
        <label>
          Enter 70 characters for the board:
          {/* 입력값을 받는 input 요소입니다. */}
          <input
            type="text"
            maxLength={numRows * numCols * 2}
            value={inputSequence}
            onChange={(e) => handleInputChange(e.target.value)}
          />
          {/* 입력값을 처리하는 버튼입니다. */}
          <button onClick={handleInputSubmit}>Submit</button>
        </label>
      </div>
    </div>
  );
};

export default ScheduleCd;
