function App() {
  const handleRecordingStop = (recordingIndex) => {
    // 중지된 시점의 stringArray 및 recordingIndex를 콘솔에 출력
    console.log("Recording stopped at index:", recordingIndex);
    console.log("Corresponding stringArray:", stringArray[recordingIndex]);
  };

  return (
    <div>
      <ScheduleCd />
      <AudioRecord onRecordingStop={handleRecordingStop} />
    </div>
  );
}

export default App;
