// App.js
import React, { useState } from "react";
import ScheduleCd from "./ScheduleCd";
import AudioRecord from "./audioRecode";

function App() {
  const [recordingIndex, setRecordingIndex] = useState(0);
  const [stringIndex, setStringIndex] = useState(
    "HHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCC"
  );

  const handleRecordingStop = ({ recordingIndex, stringIndex }) => {
    setRecordingIndex(recordingIndex);
    setStringIndex(stringIndex);
  };

  return (
    <div>
      <ScheduleCd isPaused={false} />
      <AudioRecord
        onRecordingStop={handleRecordingStop}
        stringIndex={stringIndex}
      />
      {/* 녹음 버튼이 눌릴 때마다 recordingIndex와 stringIndex를 업데이트 */}
    </div>
  );
}

export default App;
