// App.js

import "./App.css";
import React, { useState } from "react";
import ScheduleCd from "./ScheduleCd";
import AudioRecord from "./audioRecode";

function App() {
  const [isPaused, setIsPaused] = useState(false);
  const [stringIndex, setStringIndex] = useState(0); // stringIndex 추가

  const stringArray = [
    "HHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCC",
    "HHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHRRRRR",
    "HHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHPPPPP",
    "HHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHCCCCCHHHHHHHHHH",
    // ... (원하는 문자열을 추가해주세요)
  ];

  const handleRecordingStop = ({ recordingIndex, stringIndex }) => {
    // 중지된 시점의 stringArray 및 recordingIndex를 콘솔에 출력
    console.log("Recording stopped at index:", recordingIndex);
    console.log("Corresponding stringArray:", stringArray[stringIndex + 1]);

    // 화면 업데이트 중지 해제
    setIsPaused(false);

    // stringIndex 업데이트
    setStringIndex(stringIndex + 1);
  };

  return (
    <div>
      <ScheduleCd isPaused={isPaused} />
      <AudioRecord
        onRecordingStop={handleRecordingStop}
        setIsPaused={setIsPaused}
        stringIndex={stringIndex}
      />
    </div>
  );
}

export default App;
